          toolName: 'dependent-plugin',
          executionTime: 100,
          status: 'success' as const,
          issues: [],
          metrics: {
            issuesCount: 0,
            errorsCount: 0,
            warningsCount: 0,
            infoCount: 0,
            fixableCount: 0,
            score: 100
          }
        }),
