        getMetrics: () => ({
          executionCount: 0,
          totalExecutionTime: 0,
          averageExecutionTime: 0,
          successCount: 0,
          errorCount: 0
        })
      };

      mockPlugin.initialize = async () => {};
    });

    it('should initialize plugins successfully', async () => {
      await pluginManager.registerPlugin(mockPlugin);

      const pluginConfigs = {
        'test-plugin': {
          name: 'test-plugin',
          enabled: true,
          config: {
            timeout: 5000
          }
        }
      };

      await pluginManager.initializePlugins(pluginConfigs);
      // Plugin initialization called with correct configuration
      expect(pluginManager.getPlugin('test-plugin')).toBeDefined();
    });

    it('should handle initialization errors', async () => {
      const failingPlugin: AnalysisPlugin = {
        name: 'failing-plugin',
        version: '1.0.0',
        initialize: async () => {
          throw new Error('Initialization failed');
        },
        execute: async () => ({
          toolName: 'failing-plugin',
          executionTime: 100,
          status: 'error' as const,
          issues: [{
            id: 'init-error',
            type: 'error' as const,
            toolName: 'failing-plugin',
            filePath: '',
            lineNumber: 0,
            message: 'Initialization failed',
            fixable: false,
            score: 100
          }],
          metrics: {
            issuesCount: 1,
            errorsCount: 1,
            warningsCount: 0,
            infoCount: 0,
            fixableCount: 0,
            score: 0
          }
        }),
        getDefaultConfig: () => ({
