  async createPerformanceOptimizedEngine(): Promise<IssuePrioritizationEngine> {
    const config: Partial<PrioritizationConfiguration> = {
      algorithm: 'weighted',
      mlSettings: {
        enabled: false,
        confidenceThreshold: 0.8,
        retrainingThreshold: 200
      },
      rules: {
        enabled: false,
        autoOptimize: false,
